glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
  uniform properties {
      mat4 u_mat4;
      vec4 u_vec4;
      vec4 u_color;
      float u_gray;
  };
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D u_texture;
#endif
void main () {
  vec4 c1 = v_color * texture2D(texture, v_uv0)*u_mat4;
vec4 c2 = texture2D(u_texture, vec2(mod(u_vec4[0]+u_vec4[1]*v_uv0.x,1.0),mod(u_vec4[2]+u_vec4[3]*v_uv0.y,1.0)));
if(c1.a > 0.4){
    c1.rgb = vec3(1.,1.,1.);
}
  float clipAmount = c2.r + c2.g + c2.b - u_gray*3.;
  float tmp1 = step(0.0,clipAmount);
  float tmp2 = step(0.2,clipAmount);
  gl_FragColor = c1*(tmp2+(1.0-tmp2)*u_color*clipAmount*25.0)*tmp1;
}

